mapper.select:

	select  boNum,boCode, boContent, boRegDate, boTitle,boUpdateDate,boViews,boLikes,boURL,memNum,memName
from(
    select rownum rn, boNum,boCode, boContent, boRegDate, boTitle,boUpdateDate,boViews,boLikes,boURL,memNum,memName
    from (

		select	b.botitle,b.boNum,b.bocode,b.bocontent, b.boregdate, b.boupdatedate, b.boviews,b.bolikes,b.bourl,b.memnum, a.memName
		from classBoard b, member a
		where b.memNum= a.memNum and boCode =#{boCode} and boURL=#{boURL}
		order by to_number(boNum) desc
    )
    where rownum <= (#{page}*#{amount})
    )
where rn > ((#{page}-1)*#{amount})


	select  rownum rn, memName, memTel, memEmail, memLevel, memNum
from(
    select rownum rn, memName, memTel, memEmail, memLevel, memNum
    from (

		select a.memName, a.memTel, a.memEmail, a.memLevel, a.memNum
		from enrolment c, member a
		where c.memNum = a.memNum and c.openclassid=#{boURL}
		order by memName
    )
    where rownum <= (#{page}*#{amount})
    )
where rn > ((#{page}-1)*#{amount})


        select	c.openclassName
		from member a, OPENCLASS c
		where a.memNum=c.memNum and openclassid=#{boURL}

mapper.update:

update CLASSBOARD
set boViews=boViews+1
where boNum=#{boNum} and boCode=#{boCode} and boURL=#{boURL}
