mapper.insert:

insert into CLASSATTACHEDFILE(
	FILENAME,BONUM,FILEPATH,SAVEFILENAME,FILESIZE,FILECODE,CREATEDATE,MEMID,CLASSFILENUM
)
values(
	SEQ_CLASSATTACHEDFILE.NEXTVAL,#{boNum, jdbcType=VARCHAR},#{FILEPATH, jdbcType=VARCHAR},#{SAVEFILENAME, jdbcType=VARCHAR},
	#{FILESIZE, jdbcType=VARCHAR},#{FILECODE, jdbcType=VARCHAR},TO_CHAR(SYSDATE,'YYYY-MM-DD'),#{MEMID, jdbcType=VARCHAR},
	#{CLASSFILENUM, jdbcType=VARCHAR}
)

mapper.select:

	select  boNum,boCode,boTitle,boContent,boRegDate,boUpdateDate,boURL,memNum
	from(
	    select rownum rn, boNum,boCode,boTitle,boContent,boRegDate,boUpdateDate,boURL,memNum
	    from CLASSBOARD
	    where



rownum <= #{page} *#{amount}
)
where rn> (#{page}-1) *#{amount}
