mapper.select:
select DISTINCT b.memname, c.openStartDate, c.classDesc, c.classId, c.openclassId, c.classCapacity, c.classDescFile, c.memNum, c.classPrice, c.openclassName, c.subId, c.openEndDate, c.openStartTime, c.openEndTime, c.classSun, c.classMon, c.classTue, c.classWed, c.classThur, c.classFri, c.classSat from subject a , member b, openclass c
a.subcode=c.subid and c.memnum=b.memnum
and a.subCode=#{subCode}
and b.memNum=#{memNum}
and ( b.memname || c.classdesc || c.openclassname || c.openenddate || c.subid) like '%' || #{searchKey} ||'%'
order by c.openStartDate
