mapper.update:

UPDATE LECTUREPLAN
SET planCode=#{planCode}
    , planName=#{planName}
    , planLectureContent=#{planLectureContent}

    , memNum=#{memNum}
    , planCapacity=#{planCapacity}

    , planStartDate=#{planStartDate}
    , planEndDate=#{planEndDate}
    , planStartTime=#{planStartTime}
    , planEndTime=#{planEndTime}
WHERE planId = #{planId}
